import React, { useEffect, useState } from 'react';
import './styles/App.css';
import twitterLogo from './assets/twitter-logo.svg';

// Constants
const TWITTER_HANDLE = 'OTO-SUN';
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;

const App = () => {
	// „É¶„Éº„Ç∂„Éº„ÅÆ„Ç¶„Ç©„É¨„ÉÉ„Éà„Ç¢„Éâ„É¨„Çπ„ÇístateÁÆ°ÁêÜ„Åó„Å¶„ÅÑ„Åæ„Åô
	const [currentAccount, setCurrentAccount] = useState();
	// „Ç¶„Ç©„É¨„ÉÉ„Éà„ÅÆÊé•Á∂ö„ÇíÁ¢∫Ë™ç„Åó„Åæ„Åô„ÄÇ
	const checkIfWalletIsConnected = async () => {
	  // window.ethereum„ÅÆË®≠ÂÆö„ÄÇ„Åì„ÅÆË°®Ë®òÊ≥ï„ÅØJavascript„ÅÆ„ÄåÂàÜÂâ≤‰ª£ÂÖ•„Äç„ÇíÂèÇÁÖß„ÄÇ
	  const { ethereum } = window;

	  if (!ethereum) {
		console.log("Make sure you have MetaMask!");
		return;
	  } else {
		console.log("We have the ethereum object", ethereum);
	  }

	  // „É¶„Éº„Ç∂„Éº„ÅÆ„Ç¶„Ç©„É¨„ÉÉ„Éà„Ç¢„Éâ„É¨„Çπ„Çí„É™„ÇØ„Ç®„Çπ„Éà„Åó„Åæ„Åô„ÄÇ
	  const accounts = await ethereum.request({ method: "eth_accounts" });

	  // „É¶„Éº„Ç∂„Éº„ÅåË§áÊï∞„ÅÆ„Ç¢„Ç´„Ç¶„É≥„Éà„ÇíÊåÅ„Å£„Å¶„ÅÑ„ÇãÂ†¥Âêà„ÇÇ„ÅÇ„Çä„Åæ„Åô„ÄÅÊúÄÂàù„ÅÆ„Ç¢„Éâ„É¨„Çπ„Çí‰Ωø„ÅÑ„Åæ„Åô
	  if (accounts.length !== 0) {
		const account = accounts[0];
		console.log("Found an authorized account:", account);
		setCurrentAccount(account);
	  } else {
		console.log("No authorized account found");
	  }
	};

	// „Åæ„Å†„Ç¶„Ç©„É¨„ÉÉ„Éà„Å´Êé•Á∂ö„Åï„Çå„Å¶„ÅÑ„Å™„ÅÑÂ†¥Âêà„ÅÆ„É¨„É≥„ÉÄ„É™„É≥„Ç∞„Åß„Åô„ÄÇ
	const renderNotConnectedContainer = () => (
	  <div className="connect-wallet-container">
		<img
		  src="https://media.giphy.com/media/3ohhwytHcusSCXXOUg/giphy.gif"
		  alt="Ninja gif"
		/>
		<button className="cta-button connect-wallet-button">
		  Connect Wallet
		</button>
	  </div>
	);

	// „Éö„Éº„Ç∏„Åå„É™„É≠„Éº„Éâ„Åï„Çå„Çã„Å®Âëº„Å≥Âá∫„Åï„Çå„Åæ„Åô„ÄÇ
	useEffect(() => {
	  checkIfWalletIsConnected();
	}, []);

	return (
	  <div className="App">
		<div className="container">
		  <div className="header-container">
			<header>
			  <div className="left">
				<p className="title">üê±‚Äçüë§ Ninja Name Service</p>
				<p className="subtitle">Your immortal API on the blockchain!</p>
			  </div>
			</header>
		  </div>

		  {/* render Èñ¢Êï∞„Çí„Åì„Åì„Å´ËøΩÂä†„Åó„Åæ„Åô */}
		  {renderNotConnectedContainer()}

		  <div className="footer-container">
			<img alt="Twitter Logo" className="twitter-logo" src={twitterLogo} />
			<a
			  className="footer-text"
			  href={TWITTER_LINK}
			  target="_blank"
			  rel="noreferrer"
			>
			  {`built with @${TWITTER_HANDLE}`}
			</a>
		  </div>
		</div>
	  </div>
	);
  };
export default App;
